    static class RightsValues {
        private final Map<String, String> oreAggregation = new HashMap<>();
        private final boolean checkEdmWebResource;

        public RightsValues(List<String> invalidOreAggregations, List<String> validOreAggregations, boolean checkEdmWebResource) {
            this.checkEdmWebResource = checkEdmWebResource;
            for (int i = 0; i < invalidOreAggregations.size(); i++) {
                this.oreAggregation.put(invalidOreAggregations.get(i), validOreAggregations.get(i));
            }
        }

        public Map<String, String> getOreAggregation() {
            return oreAggregation;
        }

        public boolean isCheckEdmWebResource() {
            return checkEdmWebResource;
        }
    }
